import React,{Component} from 'react';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import Logo from './components/Logo/Logo'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition'
import 'tachyons';

const app = new Clarifai.App({
  apiKey: 'b81051f4139a4a1dbddf3d9f99b095f4'
 });


const particlesOptions={
  particles: {
    number: {
      value: 100,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends Component{
  constructor(){
    super();
    this.state={
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin',
      isSignedIn: false,
      user: {
        id: '',
        name: '',
        email: '',
        entries: 0,
        joined: ''
      }
    }
  }

  loadUser =(data) =>{
    this.setState({
      user: {
        id: data.id,
        name: data.name,
        email: data.email,
        entries: data.entries,
        joined: data.joined
      }
    })
  }

  // componentDidMount(){
  //   fetch('http://localhost:3000/')
  //     .then(response=> response.json())
  //     .then(console.log)
  // }

  calculateFaceLocation= (data)=>{
    const clarifaiFace =  data.outputs[0].data.regions[0].region_info.bounding_box;
    const image= document.getElementById("inputimage");
    const width= Number(image.width);
    const height= Number(image.height);
    console.log(width,height);
    return{
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height) 
    }
  }
  displayFaceBox= (box)=>{
    console.log(box);
    this.setState({box: box});
  }
  onInputChange =(event)=>{
    this.setState({input: event.target.value});
  }


//onPictureSubmit name looks better then onButtonSubmit
  onButtonSubmit =()=>{
    this.setState({imageUrl: this.state.input});
    // app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
    // app.models.predict(Clarifai.CELEBRITY_MODEL, this.state.input)
    // .then(
    //   function(response){
    //     console.log(response); 475328
    //   }
    // )
    app.models.predict(
    {
      id: "f76196b43bbd45c99b4f3cd8e8b40a8a",
      version: "5e026c5fae004ed4a83263ebaabec49e",
    },this.state.input
  )
  .then((response)=>{
    if(response){
      fetch('http://localhost:3000/image',{
        method: 'put',
        headers: {'Content-type': 'application/json'},
        body: JSON.stringify({
            id: this.state.user.id
        })
      })
      .then(response =>response.json())
      .then(count =>{
        //refer video 301, just after 7:32
        console.log(count);
        this.setState(Object.assign(this.state.user,{entries: count }))
      })
    }
    this.displayFaceBox(this.calculateFaceLocation(response))
    })
  .catch(err=>console.log(err));
  }

  onRouteChange =(route) =>{
    if(route === 'signout'){
      this.setState({isSignedIn: false})
    }
    else if(route === 'home'){
      this.setState({isSignedIn: true})
    }
    this.setState({route: route});
  }

  //for understanding .then func syntax google: .then method in javascript.
        //console.log(response.outputs[0].data.regions[0].region_info.bounding_box);
        //console.log(response);

    render(){
      // use of destructuring
      // const {isSignedIn,imageUrl, etc} = this.state;
      // "this.state.isSignedIn" can be written as only "isSignedIn"
    return (
      <div className="App">
        <Particles className="particles"
              params={particlesOptions}
            />
        <Navigation isSignedIn={this.state.isSignedIn} onRouteChange={this.onRouteChange}/>
        {this.state.route === 'home'
        ? <div>
            <Logo/>
            <Rank name={this.state.user.name} entries={this.state.user.entries}/>
            <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
            <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl}/>
          </div> 
          : (
            this.state.route === 'signin' ? <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange}/> : <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
            )
        }
      </div>
    );
  }
}

export default App;
